<script>
import axios from "axios-https-proxy-fix";
import { AtomSpinner } from "epic-spinners";
import Avatar from "vue-avatar";
import aclookupTour from "../components/Tours/aclookupTour";

const auth = {
  headers: {
    Authorization: "Bearer " + localStorage.getItem("CUSER"),
  },
};

export default {
  name: "ACList",
  components: {
    AtomSpinner,
    Avatar,
    aclookupTour,
  },
  data() {
    return {
      server: process.env.VUE_APP_SERVER_URL,
      loading: true,
      acList: [],
      dupACList: [],
      search: "",
      selectedCat: "Show All",
      selectedState: "",
      countOfPage: 10,
      currPage: 1,
      noACFound: false,
      step: 3,
      hoveredState: "",
      mode: "name",
    };
  },
  filters: {
    Upper(value) {
      return value.toUpperCase();
    },
    Lower(value) {
      return value.toLowerCase();
    },
    json(value) {
      return JSON.stringify(value);
    },
  },
  methods: {
    firstLoad: function () {
      this.loading = true;
      this.$store.commit("setActivePage", "tools");
      document
        .getElementById("mobileScrollTarget")
        .scrollIntoView({ behavior: "smooth" });
      return axios.get(`${this.server}/api/v1/ac-territories`, auth).then(
        (response) => {
          if (response.status === 401) {
            Vue.$router.push({ name: "Login" });
          } else {
            this.acList = response.data.aCDistributors;
            this.loading = false;
          }
        },
        (error) => {
          this.$router.push("/");
        }
      );
      function fn(e) {
        for (var i = tooltip.length; i--; ) {
          tooltip[i].style.left = e.pageX + "px";
          tooltip[i].style.top = e.pageY + "px";
        }
      }
    },

    uniqueRegionGroupNames: function (companyName) {
      let uniqueRegionGroupNameForCompany = [];
      const entry = this.acList.filter((x) => x.Name === companyName)[0];

      if (entry) {
        const regionsForCompany = entry.ACDistributorRegions;
        uniqueRegionGroupNameForCompany = new Set(
          regionsForCompany.map((x) => x.RegionGroupName)
        );
        uniqueRegionGroupNameForCompany = [
          ...uniqueRegionGroupNameForCompany,
        ].sort();
      }

      return uniqueRegionGroupNameForCompany;
    },

    uniqueRegionNames: function (companyName, regionGroupName) {
      let uniqueRegionNamesForCompany = [];

      const entry = this.acList.filter((x) => x.Name === companyName)[0];

      if (entry) {
        const regionsForCompany = entry.ACDistributorRegions;
        uniqueRegionNamesForCompany = new Set(
          regionsForCompany
            .filter((y) => y.RegionGroupName === regionGroupName)
            .map((x) => x.RegionName)
        );
        uniqueRegionNamesForCompany = [...uniqueRegionNamesForCompany].sort();
      }

      return uniqueRegionNamesForCompany;
    },

    stripAddress: function (address) {
      //Salesforce data seems to have no rhyme or reason and randomly outputs http:// and https:// this strips that
      let newAddress = address.replace(/http:|https:/g, "");
      let newAddress2 = newAddress.replace(/[/]/g, "");
      return newAddress2;
    },

    searchReset: function () {
      this.selectedCat = "Show All";
      this.selectedState = "";
      this.currPage = 1;
      document
        .getElementById("mobileScrollTarget")
        .scrollIntoView({ behavior: "smooth" });
    },

    setPage: function (idx) {
      this.search = "";
      if (idx <= 0 || idx > this.totalPage) {
        return;
      }
      this.currPage = idx;
    },

    clickState: function (tState) {
      this.currPage = 1;
      //console.log(tState);
      this.selectedState = tState;
      this.step = 3;
      document
        .getElementById("mobileScrollTarget")
        .scrollIntoView({ behavior: "smooth" });
    },

    showState(name) {
      this.hoveredState = name;
    },
    clearState() {
      this.hoveredState = "";
    },
    showList: function () {},
    showUSMap: function () {
      this.step = 1;
      this.selectedState = "";
      document
        .getElementById("mobileScrollTarget")
        .scrollIntoView({ behavior: "smooth" });
    },
    showCAMap: function () {
      this.step = 2;
      this.selectedState = "";
      document
        .getElementById("mobileScrollTarget")
        .scrollIntoView({ behavior: "smooth" });
    },
    showAll: function () {
      this.step = 3;
      this.selectedState = "";
      document
        .getElementById("mobileScrollTarget")
        .scrollIntoView({ behavior: "smooth" });
    },
    foundFirework(location) {
      this.$store.commit("FOUND_FIREWORK", location);
    },
  },
  computed: {
    byStateList() {
      // You can do a check on the variable called 'found'. If it is empty you can delete the entry from the copy.
      // Make a copy to not ruin the original data
      const copy = Object.assign(this.acList, {});

      copy.forEach((entry) => {
        const found = entry.ACDistributorRegions.filter(
          (region) => region.RegionGroupName === this.selectedState
        );

        // Replace the current list with the filtered list
        entry.Regions = found;
      });

      return copy.filter((entry) => entry.Regions.length > 0);
    },

    activeNav() {
      return this.$store.state.activePage;
    },
    states() {
      return new Set(this.acList.map((x) => x.State).sort());
    },

    filteredACs: function () {
      const self = this;
      //set the current page to 0
      this.currPage = 1;
      //if the search box is filled in

      if (this.search.trim() !== "") {
        return this.acList.filter((e) => {
          //filter the subject line by what is filled in the search box

          return e.Name.toLowerCase().indexOf(this.search.toLowerCase()) > -1;
        });
      } else {
        return this.acList;
      }
    },

    foundNoIdea: function () {
      if (this.search.length > 1 && this.filteredACs.length === 0) {
        return true;
      }
    },

    pageStart: function () {
      return (this.currPage - 1) * this.countOfPage;
    },

    totalPage: function () {
      return Math.ceil(this.acList.length / this.countOfPage);
    },
  },
  created() {
    this.$store.commit("showNavigation");
  },
  mounted() {
    this.firstLoad();
  },
};
</script>

